content:
.menu-trigger{
    //ハンバーガーアイコンの描写
    width: 50px;
    height: 44px;
    transition: all .4s;
    box-sizing: border-box;
    span {
        display: inline-block;
        transition: all .4s;
        box-sizing: border-box;
        position: absolute;
        left: 0;
        width: 100%;
        height: 4px;
        background-color: black;
        border-radius: 4px;
    }
    span:nth-of-type(1) {
        top: 0;
      }
    span:nth-of-type(2) {
        top: 20px;
      }
    span:nth-of-type(3) {
        bottom: 0;
      }
    .active span:nth-of-type(1) {
        -webkit-transform: translateY(20px) rotate(-45deg);
        transform: translateY(20px) rotate(-45deg);
    }
    .active span:nth-of-type(2) {
        opacity: 0;
    }
    .active span:nth-of-type(3) {
        -webkit-transform: translateY(-20px) rotate(45deg);
        transform: translateY(-20px) rotate(45deg);
    }
}
